import React, {useState, useEffect, useRef} from 'react';
import {
    StyleSheet,
    Text,
    View,
    TouchableOpacity,
    Animated,
    Image,
    TextInput
} from 'react-native';
import {Accelerometer} from 'expo-sensors';
import {Camera} from 'expo-camera';

export default function App() {
    const [steps, setSteps] = useState(0);
    const [isCameraVisible, setIsCameraVisible] = useState(false);
    const [hasCameraPermission, setHasCameraPermission] = useState(null);
    const [progress] = useState(new Animated.Value(0));
    const [isMoving, setIsMoving] = useState(false);
    const [capturedImage, setCapturedImage] = useState(null);
    const [isTakingPicture, setIsTakingPicture] = useState(false);
    const cameraRef = useRef(null);

    useEffect(() => {
        // Check camera permission and set state
        const getCameraPermission = async () => {
            const {status} = await Camera.requestCameraPermissionsAsync();
            setHasCameraPermission(status === 'granted');
        };
        getCameraPermission();

        // Subscribe to accelerometer sensor
        Accelerometer.addListener(accelerometerData => {
            const {x, y, z} = accelerometerData;
            const acceleration = Math.sqrt(x * x + y * y + z * z);
            if (acceleration > 1.2) {
                // If the device is moving, increment the step count
                setIsMoving(true);
            } else {
                setIsMoving(false);
            }
        });

        // Unsubscribe from accelerometer sensor on unmount
        return() => {
            Accelerometer.removeAllListeners();
        };
    }, []);

    useEffect(() => {
        if (isMoving) {
            setSteps(steps => steps + 1);
        }
    }, [isMoving]);

    useEffect(() => {
        // Animate progress bar
        Animated
            .timing(progress, {
                toValue: steps / 10,
                duration: 1000,
                useNativeDriver: false
            })
            .start();
    }, [steps, progress]);

    const handleCameraButtonPress = () => {
        setIsCameraVisible(true);
        setIsTakingPicture(false);
        setCapturedImage(null);
    };

    const handleCapture = async () => {
        setIsTakingPicture(true);
        if (cameraRef.current) {
            const photo = await cameraRef
                .current
                .takePictureAsync();
            setCapturedImage(photo);
        }
        setIsTakingPicture(false);
    };

    const handleQuitButtonPress = () => {
        setIsCameraVisible(false);
        setSteps(0);
    };

    return (
        <View style={styles.container}>
            <Image source={require('./duck.gif')} style={styles.gif}/>
            <View style={styles.cylinder}>
                <Animated.View
                    style={[
                        styles.cylinderFill, {
                            right: progress.interpolate({
                                inputRange: [
                                    0, 1
                                ],
                                outputRange: ['100%', '0%']
                            })
                        }
                    ]}/>
            </View>
            <View style={styles.textContainer}>
                <Text style={styles.text}>Steps: {steps}</Text>
                <Text style={styles.text}>Goal: 10</Text>
            </View>
            {
                hasCameraPermission === null || steps < 10
                    ? null
                    : (
                        <TouchableOpacity
                            style={styles.cameraButton}
                            onPress={handleCameraButtonPress}
                            disabled={!hasCameraPermission}>
                            <Text style={styles.cameraButtonText}>Open Camera</Text>
                        </TouchableOpacity>
                    )
            }
            {
                isCameraVisible
                    ? (
                        <View style={styles.cameraContainer}>
                            <Camera style={styles.camera} ref={cameraRef}/>
                            <View style={styles.buttonContainer}>
                                <TouchableOpacity
                                    style={styles.captureButton}
                                    onPress={handleCapture}
                                    disabled={isTakingPicture}>
                                    <Text style={styles.captureButtonText}>
                                        {
                                            isTakingPicture
                                                ? 'Capturing...'
                                                : 'Capture'
                                        }
                                    </Text>
                                </TouchableOpacity>
                                <TouchableOpacity style={styles.quitButton} onPress={handleQuitButtonPress}>
                                    <Text style={styles.quitButtonText}>Quit</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    )
                    : null
            }
            {
                capturedImage && (
                    <View style={styles.previewContainer}>
                        <Text style={styles.centeredText}>Today's 오운완</Text>
                        <Image
                            source={{
                                uri: capturedImage.uri
                            }}
                            style={styles.previewImage}/>
                    </View>
                )
            }
            </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center'
    },
    gif: {
        width: 60,
        height: 70,
        marginBottom: 10
    },
    cylinder: {
        width: '90%',
        height: 40,
        borderRadius: 10,
        borderWidth: 3,
        borderColor: '#4CAF50',
        alignItems: 'center',
        justifyContent: 'flex-end',
        marginBottom: 20,
        overflow: 'hidden'
    },
    cylinderFill: {
        backgroundColor: '#4CAF50',
        position: 'absolute',
        bottom: 0,
        top: 0,
        left: 0,
        right: '100%'
    },
    textContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '80%'
    },
    text: {
        fontSize: 18
    },
    cameraButton: {
        backgroundColor: '#4CAF50',
        padding: 10,
        borderRadius: 10
    },
    cameraButtonText: {
        color: '#fff',
        fontSize: 18
    },
    cameraContainer: {
        flex: 1,
        width: '100%',
        height: '100%',
        position: 'absolute',
        top: 0,
        left: 0,
        backgroundColor: 'black',
        alignItems: 'center',
        justifyContent: 'center'
    },
    camera: {
        //flex: 1,
        width: 380,
        height: 500
    },
    buttonContainer: {
        flexDirection: 'row'
    },
    quitButton: {
        backgroundColor: '#FF0000',
        padding: 10,
        borderRadius: 10,
        marginTop: 20
    },
    quitButtonText: {
        color: '#fff',
        fontSize: 18
    },
    captureButton: {
        backgroundColor: '#4CAF50',
        padding: 10,
        borderRadius: 10,
        marginTop: 20,
        marginRight: 30
    },
    captureButtonText: {
        color: '#fff',
        fontSize: 18
    },
    previewContainer: {
        top: 5
        //marginTop: 20
    },
    centeredText: {
        fontSize: 24,
        textAlign: 'center',
        marginTop: 10,
        marginBottom: 10
    },
    previewImage: {
        width: 150,
        height: 150,
        borderRadius: 0
    }
});
